function measureCRP(){var t=window.performance.timing,e=t.domInteractive-t.domLoading,n=t.domContentLoadedEventStart-t.domLoading,o=t.domComplete-t.domLoading,d=document.getElementById("crp-stats");d.textContent="Interactive: "+e+"ms. DCL: "+n+"ms. Complete: "+o+"ms."}window.addEventListener("load",function(t){measureCRP()});